// Simulate authentication service
const Auth = {
  _isAuthenticated: false,

  authenticate(name, pass, cb) {
    this._isAuthenticated = true;
    // setTimeout(
    //   () =>
    //     cb({
    //       name: name
    //     }),
    //   100
    // );

    API.create("/rating", {
      username : name,
      passwd : pass,
    })
      .then(res => {
        if (res.result) {
          console.log("===> rating", rating);

          this.setState({
            rating : rating
          });
        }
      })
      .catch(err => console.log(err));
  },

  signout(cb) {
    this._isAuthenticated = false;
    setTimeout(cb, 100);
  }
};

export default Auth;
