import React, { Component } from "react";
import Button from "@material-ui/core/Button";
import AddShoppingCartIcon from "@material-ui/icons/AddShoppingCart";
import CircularProgress from "@material-ui/core/CircularProgress";
import { addItemInCart } from "../../Redux/Actions";
import API from '../../helpers/api.js';
import Item from "../Item/Item";
import { connect } from "react-redux";
import Rating from 'react-rating';

const USER_ID = 2;
class ConnectedDetails extends Component {
  constructor(props) {
    super(props);

    this.isCompMounted = false;

    this.state = {
      relatedItems: [],
      quantity: 1,
      item: null,
      rating : 0,
      loading: false
    };
  }

  // Gets product and its related items based on product ID.
  async fetchProductUsingID(id) {
    this.setState({ loading: true });
    var item = [];
    API.get('/recipes', {id : id})
    .then((res) => {
      if (res.data.rows.length) {        
        item = res.data.rows[0];
        var date = new Date(item.createDate).toLocaleDateString();
        item.createDate = date;
    if (this.isCompMounted) {
      this.setState({
        item,
        quantity:1,
        loading: false,
      }, () =>     this.getRating() )}
      }
    })
    .catch(err => console.log(err));

    API.get('/findSimilarRecipe', {rid : id})
    .then((res) => {
      if (res.data.rows.length) {        
        var relatedItems = res.data.rows;
            // Make sure this component is still mounted before we set state..
      if (this.isCompMounted) {
        this.setState({
          relatedItems,
          // relatedItems: relatedItems.data.filter(x => x.id !== item.id),
          loading: false,
        })}
      }
    })
    .catch(err => console.log(err));
  }


  getRating() {

    API.get('/rating', {rid : this.state.item.id, uid : USER_ID})
    .then((res) => {
      if (res.data.rows.length) {        
        this.setState({
          rating : res.data.rows[0].rating
        })
      }
    })
    .catch(err => console.log(err));
  }


  componentDidUpdate(prevProps, prevState, snapshot) {

    // If ID of product changed in URL, refetch details for that product
    if (this.props.match.params.id !== prevProps.match.params.id) {
      this.fetchProductUsingID(this.props.match.params.id);
    }

  }


  componentDidMount() {
    this.isCompMounted = true;
    this.fetchProductUsingID(this.props.match.params.id);
  }

  componentWillUnmount() {
    this.isCompMounted = false;
  }

  rateRecipe(rating) {
    API.create('/rating', {
      rid : this.state.item.id,
      uid : USER_ID,
      rating, rating
    })
    .then((res) => {

    })
    .catch(err => console.log(err));
  }

  render() {    
    if (this.state.loading) {
      return <CircularProgress className="circular" />;
    }

    if (!this.state.item) {
      return null;
    }
    return (
      <div style={{ padding: 10 }}>
        <div
          style={{
            marginBottom: 20,
            marginTop: 10,
            color: "gray",
            fontSize: 20
          }}
        >
          {this.state.item.name}
        </div>
        <div style={{ display: "flex" }}>
          {/* <img src={this.state.item.imageUrls[0]} alt="" width={250} height={250} style={{ borderRadius: "5%", objectFit: "cover" }} /> */}
          <div
            style={{
              flex: 1,
              marginLeft: 20,
              display: "flex",
              flexDirection: "column"
            }}
          >
              Rate it
            <Rating
              initialRating = {this.state.rating}
              style={{ width: 200, marginTop: 5 }}
              color="primary"
              variant="outlined"
              onClick={(v) => this.rateRecipe(v)}
            />
          </div>
        </div>

        {/* Recipe description */}
        <div
          style={{
            marginTop: 30,
            marginBottom: 20,
            color: "gray",
            fontSize: 20
          }}
        >
          Recipe Description
        </div>
        <div
          style={{
            marginLeft: 5,
            maxHeight: 200,
            fontSize: 13,
            overflow: "auto"
          }}
        >
        </div>
        <div style={{ fontSize: 18, marginTop: 10 }}>
              Steps: {this.state.item.detailStep}
            </div>
            <div style={{ fontSize: 18, marginTop: 10 }}>
              Created Date: {this.state.item.createDate}
            </div>
            <div style={{ fontSize: 18, marginTop: 10 }}>
              Calorie: {this.state.item.calorie}
            </div>

        {/* Relateditems */}
        <div
          style={{
            marginTop: 30,
            marginBottom: 10,
            color: "gray",
            fontSize: 20
          }}
        >
          Related Items
        </div>
        {
           JSON.stringify(this.state.relatedItems[0]) != '{}' ? this.state.relatedItems.map(x => {
            return <Item key={x.id} item={x} />;
          }) : null
        }
      </div >
    );
  }
}

let Details = connect()(ConnectedDetails);
export default Details;
