import React, { Component } from "react";
import Button from "@material-ui/core/Button";
import AddShoppingCartIcon from "@material-ui/icons/AddShoppingCart";
import CircularProgress from "@material-ui/core/CircularProgress";
import { addItemInCart } from "../../Redux/Actions";
import API from '../../helpers/api.js';
import Item from "../Item/Item";
import { connect } from "react-redux";
import TextField from "@material-ui/core/TextField";



class ConnectedDetails extends Component {
  constructor(props) {
    super(props);

    this.isCompMounted = false;

    this.state = {
      relatedItems: [],
      quantity: 1,
      item: null,
      loading: false
    };
  }

  // Gets product and its related items based on product ID.
  async fetchProductUsingID(id) {
    this.setState({ loading: true });
    var item = [];
    API.get('/recipes', {id : id})
    .then((res) => {
      if (res.data.rows.length) {        
        item = res.data.rows[0];
        console.log(item);
            // Make sure this component is still mounted before we set state..
    if (this.isCompMounted) {
      this.setState({
        item,
        quantity:1,
        // relatedItems: relatedItems.data.filter(x => x.id !== item.id),
        loading: false,
      })}
      }
    })
    .catch(err => console.log(err));
  }

  componentDidUpdate(prevProps, prevState, snapshot) {

    // If ID of product changed in URL, refetch details for that product
    if (this.props.match.params.id !== prevProps.match.params.id) {
      this.fetchProductUsingID(this.props.match.params.id);
    }

  }


  componentDidMount() {
    this.isCompMounted = true;
    this.fetchProductUsingID(this.props.match.params.id);
  }

  componentWillUnmount() {
    this.isCompMounted = false;
  }



  render() {
    console.log(this.state.item);

    if (this.state.loading) {
      return <CircularProgress className="circular" />;
    }

    if (!this.state.item) {
      return null;
    }
    return (
      <div style={{ padding: 10 }}>
        <div
          style={{
            marginBottom: 20,
            marginTop: 10,
            color: "gray",
            fontSize: 20
          }}
        >
          {this.state.item.name}
        </div>
        <div style={{ display: "flex" }}>
          {/* <img src={this.state.item.imageUrls[0]} alt="" width={250} height={250} style={{ borderRadius: "5%", objectFit: "cover" }} /> */}
          <div
            style={{
              flex: 1,
              marginLeft: 20,
              display: "flex",
              flexDirection: "column"
            }}
          >


            <Button
              style={{ width: 200, marginTop: 5 }}
              color="primary"
              variant="outlined"
              onClick={() => {
                this.props.dispatch(
                  addItemInCart({
                    ...this.state.item,
                    quantity: this.state.quantity
                  })
                );
              }}
            >
              Start Cooking 
            </Button>
          </div>
        </div>

        {/* Recipe description */}
        <div
          style={{
            marginTop: 30,
            marginBottom: 20,
            color: "gray",
            fontSize: 20
          }}
        >
          Recipe Description
        </div>
        <div
          style={{
            marginLeft: 5,
            maxHeight: 200,
            fontSize: 13,
            overflow: "auto"
          }}
        >
          {this.state.item.detailStep ? this.state.item.detailStep :  "Not available" }
        </div>

        {/* Relateditems */}
        <div
          style={{
            marginTop: 30,
            marginBottom: 10,
            color: "gray",
            fontSize: 20
          }}
        >
          Related Items
        </div>
        {
          this.state.relatedItems.slice(0, 3).map(x => {
            return <Item key={x.id} item={x} />;
          })
        }
      </div >
    );
  }
}

let Details = connect()(ConnectedDetails);
export default Details;
