import React, { Component } from "react";
import { withRouter, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import Auth from "../../../Auth";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { setLoggedInUser } from "../../../Redux/Actions";
import Avatar from "@material-ui/core/Avatar";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined";
import cookie from "react-cookies";
import API from "../../../helpers/api.js";

class Step2 extends Component {
  state = {
    tags: [],
    selectedTags: [],
    redirectToReferrer: false
  };

  componentDidMount() {
    API.get("/recipe_tag")
      .then(res => {
        this.setState({
          tags: res.data.rows
        });
      })
      .catch(err => console.log(err));
  }

  render() {
    const { from } = this.props.location.state || { from: { pathname: "/" } };

    // If user was authenticated, redirect her to where she came from.
    if (this.state.redirectToReferrer === true) {
      return <Redirect to={from} />;
    }
    console.log(this.state.tags);

    return (
      <div
        style={{
          flex : 1,
          // height: "100%",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          flexDirection : 'column',
          marginBottom : '30%'
        }}
      >
        <div
          style={{
            // height: 300,
            width: '100%',
            padding: 30,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            flexDirection: "column"
          }}
        >
          <div
            style={{
              marginBottom: 20,
              fontSize: 24,
              textAlign: "center"
            }}
          >
            {" "}
            What you like?{" "}
          </div>



          <Button
            style={{ marginTop: 20, width: 200 }}
            variant="outlined"
            color="primary"
            onClick={() => {
              API.create(
                "/register",
                {
                  username: this.state.userName,
                  passwd: this.state.pass
                },
                false
              ).then(data => {
                if (data.result) {
                  console.log(data.data);
                } else {
                  console.log("failed");
                }
              });
            }}
          >
            Start Cooking
          </Button>
          {this.state.wrongCred && (
            <div style={{ color: "red" }}>Wrong username and/or password</div>
          )}
        </div>

        <div style = {{
          display             : "grid",
          gridGap             : "1%",
          gridTemplateColumns : "auto auto"
        }}>
          {this.state.tags.length > 0 ? (
            this.state.tags.map(t => {
              return (
                <Button
                  style={{ marginTop: 20, width: 200, flex: 1 }}
                  variant="outlined"
                  color= {!this.state.selectedTags.includes(t.recipeTagTypeName) ? "primary"  : '#000'}
                  onClick = {() =>{
                    var selectedTags = this.state.selectedTags;
                    // selectedTags = 
                    selectedTags.includes(t.recipeTagTypeName) ?
                    selectedTags.splice(selectedTags.indexOf(t.recipeTagTypeName), 1) :
                    selectedTags.push(t.recipeTagTypeName);
                    console.log(selectedTags);

                    // this.setState(selectedTags)
                  }}
                >
                  {t.recipeTagTypeName}
                </Button>
              );
            })
          ) : null}
</div>
      </div>
    );
  }
}
const RegisterStep2 = withRouter(connect()(Step2));

export default RegisterStep2;
