import React, { Component } from "react";
import Item from "../Item/Item";
import CircularProgress from "@material-ui/core/CircularProgress";
import queryString from "query-string";
// import Api from "../../Api";
import Paging from "../Paging/Paging";
import ProductsHeader from "../ProductsHeader/ProductsHeader"
import API from '../../helpers/api.js'
import cookie from "react-cookies";

let s = window.location.search;
let params = new URLSearchParams(s);
let search = params.get('query');

class SearchResult extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loading         : false,
      totalItemsCount : null,
      searchResult : [],
      userID: cookie.load("userId")

    };
    this.updateQueryString = this.updateQueryString.bind(this);

  }

  async fetchData() {

    this.setState({ loading: true });

    let qsAsObject = queryString.parse(this.props.location.search);
    let results = [];
    // Make the request to get items
    API.get('/search_recipes', {search : search})
    .then((res) => {
      if (res) {

        results = res.data.rows;
           // Store results in state
      this.setState({
        searchResult: results,
        loading: false,
        totalItemsCount: results.length
      });
      }
    })
    .catch(err => console.log(err))
  }



  componentDidMount() {
    this.fetchData();
  }

  updateQueryString(newValues) {
    let currentQS = queryString.parse(this.props.location.search);
    console.log('search', currentQS);

    // Create new query string by merging with old one
    let newQS = { ...currentQS, ...newValues };

    this.props.history.push("/?" + queryString.stringify(newQS));
  }

  componentDidUpdate(prevProps, prevState, snapshot) {

    let currentQS = queryString.parse(this.props.location.search);
    let oldQS = queryString.parse(prevProps.location.search)

    // Check if the query strings changed.
    let check1 = Object.entries(currentQS).some(([k, v]) => v !== oldQS[k]);
    let check2 = Object.entries(oldQS).some(([k, v]) => v !== currentQS[k]);
    let isDifferent = check1 || check2;

    // We will refetch products only when query string changes.
    if (isDifferent) {
      this.fetchData();
    }
  }

  render() {
    let parsedQS = queryString.parse(this.props.location.search);

    // Check if products are loading ...
    if (this.state.loading) {
      return (
        <CircularProgress className="circular" />
      );
    }

    return (
      <div style={{ height: "100%", display: "flex", flexDirection: "column", alignItems : 'center', marginBottom : '10%' }}>
        <ProductsHeader
          parsedQS={parsedQS}
          updateQueryString={this.updateQueryString}
          totalItemsCount={this.state.totalItemsCount} 
          />
          <div style = {{padding : '2%', paddingTop : 0}}>


        <div
          style={{
            marginTop: 30,
            marginBottom: 10,
            color: "gray",
            fontSize: 20
          }}
        >
          Most Popular
        </div>
        <div style={{ flex: 1 }}>
          {this.state.trending.length ?  this.state.trending.map(item => {
            return <Item key={item.recipeID} item={item} />;
          }) : null
        }
        </div>


          <div>
            <div
              style={{
                marginTop: 30,
                marginBottom: 10,
                color: "gray",
                fontSize: 20
              }}
            >
              Search Result
            </div>
            <div style={{ flex: 1 }}>
              {this.state.searchResult.map(item => {            
                return <Item key={item.recipeID} item={item} />;
              })}
            </div>
          </div>

        </div>
      </div >
    );
  }
}

export default SearchResult;
